DECLARE
  current_year INT;
  next_number INT;
  date_suffix VARCHAR(6);
BEGIN
  current_year := EXTRACT(YEAR FROM CURRENT_DATE);

  INSERT INTO request_number_sequence (year, last_number)
  VALUES (current_year, 1)
  ON CONFLICT (year) DO UPDATE
    SET last_number = request_number_sequence.last_number + 1
  RETURNING last_number INTO next_number;

  date_suffix := TO_CHAR(CURRENT_DATE, 'DDMMYY');

  RETURN LPAD(next_number::TEXT, 6, '0') || '_' || date_suffix;
END;
